import React from 'react';

const Index = ({articles}) => {
    console.log(articles)
    return (
        articles.map((article, index) => (
            <div key={index}>
                <div>{article.title}</div>
                <div>{article.author.username}</div>
                <img src={article.author.image} alt=''/>
            </div>
        ))

    );
};

export default Index;