import React from 'react';
import TagList from 'components/TagList'
import styles from './index.css'
import {Link} from "react-router-dom";

const Index = ({articles}) => {
    console.log(articles)
    return (
        articles.map((article, index) => (
            <div key={index} className='article-preview'>
                <div className="article-meta">
                    <Link to={`/${article.author.username}`}  className="tag-pill" key={article.author.username}>
                        <img src={article.author.image} alt=''/>
                    </Link>
                    <div className='info'>
                        <Link to={`/${article.author.username}`}  className="tag-pill author" key={article.author.username}>
                            {article.author.username}
                        </Link>
                        <span className='date'>{article.createdAt}</span>
                    </div>
                    <div className="pull-xs-right">
                            <button className="btn btn-sm btn-outline-primary"><i className="ion-heart">1</i></button>
                    </div>
                    <div className='preview-link'>
                        <Link className='preview-link' to={`article/${article.slug}`}>
                            {article.title}
                        </Link>
                    </div>
                    <p>Read more...</p>
                </div>
        
            </div>
        ))

    );

};

export default Index;