import React, { useState, useEffect } from 'react';
import styles from './index.css'
import BackendErrorMassages from 'components/backendErrorMassages'
const Index = ({onSubmit, errors, initialValues}) => {
    const [title, setTitle] = useState('')
    const [body, setBody] = useState('')
    const [description, setDescription] = useState('')
    const [tagList, setTagList] = useState('')
    const handleSubmit = event => {
        event.preventDefault()
        const article = {
            title,
            body,
            description,
            tagList
        }
        onSubmit(article)
        console.log('fields', title, body, description, tagList)
    }

    useEffect(() => {
        if (!initialValues) {   
            return 
        }
        setTitle(initialValues.title)
        setBody(initialValues.body)
        setDescription(initialValues.description)
        setTagList(initialValues.tagList.join(' '))
    }, [initialValues])
    return (
        <div className='editor-page'>
            <div className='container page'>
                <div className='row'>
                    <div className='col-md-10 offset-md-1 col-xs-12'>
                        {errors && <BackendErrorMassages BackendError={errors}/>}
                        <form onSubmit={handleSubmit}>
                            <fieldset>
                                <fieldset>
                                    <input type='text' className='form-control form-control-lg' placeholder='Article Title' value={title} onChange={e => setTitle(e.target.value)}/>
                                </fieldset>
                                <fieldset>
                                    <input type='text' className='form-control form-control-lg' placeholder='Whats this article about?' value={description} onChange={e => setDescription(e.target.value)}/>
                                </fieldset>
                                <fieldset>
                                    <input type='text' className='form-control form-control-lg' placeholder='Write your article (in markdown)' value={body} onChange={e => setBody(e.target.value)}/>
                                </fieldset>
                                <fieldset>
                                    <input type='text' className='form-control form-control-lg' placeholder='Enter tags' value={tagList} onChange={e => setTagList(e.target.value)}/>
                                </fieldset>
                                <button className="btn btn-lg pull-xs-right btn-primary" type="submit">Publish Article</button>
                            </fieldset>
                        </form>
                    </div>
                </div>
            </div>
        </div>

    )

};

export default Index;