import React from 'react';
import {range} from 'components/utils'
import {Link} from "react-router-dom";
import classNames from 'classnames';

const PaginationItem = ({page, currentPage, url}) => {

    const liClasses = classNames({
        'page-item': true,
        active: currentPage === page
    })
    return (
        <li className={liClasses}>
            <Link to={`${url}?page=${page}`} className='page-link' >
                {page}
            </Link>
        </li>
    )
}




const Index = ({articles, currentPage, url}) => {
    const pageCount = Math.ceil(articles/10)
    const pages = range(1,50)
    return (
        <div>
            <ul className='pagination'>
                {pages.map(page => (
                    <PaginationItem page={page} currentPage={currentPage} url={url} key={page}/>
                ))}

            </ul>
        </div>
    );
};

export default Index;