import React,{useState} from 'react'
import {Link} from "react-router-dom";
import useFetch from 'hooks/useFetch'

const Authentication = () => {
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')
    const [{response, isLoading, error}, doFetch] = useFetch('users/login')

    console.log(response,isLoading, error)
    const handleSubmit = (event) => {
        event.preventDefault()
        doFetch({method:'post', data: {
                user: {
                    email:'1121212@mail.ru',
                    password:'111111111'
                }
            }})
    }


    return (
        <div className="auth-login">
            <div className="col-md-6 offset-md-3 col-xs-12">
                <h1 className="text-xs-center">Login</h1>
                <p>
                    <Link to='register'>Need an account?</Link>
                </p>
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
                        <input type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"
                               value={email} onChange={event => setEmail(event.target.value)}
                        />
                            <div id="emailHelp" className="form-text">We'll never share your email with anyone else.
                            </div>
                    </div>
                    <div className="mb-3">
                        <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
                        <input type="password" className="form-control" id="exampleInputPassword1"
                        value={password} onChange={event => setPassword(event.target.value)}
                        />
                    </div>
                    <div className="mb-3 form-check">
                        <input type="checkbox" className="form-check-input" id="exampleCheck1"/>
                            <label className="form-check-label" htmlFor="exampleCheck1">Check me out</label>
                    </div>
                    <button type="submit" className="btn btn-primary" disabled={isLoading}>Submit</button>
                </form>
            </div>
        </div>
    )
}

export default Authentication